import React, { useState } from 'react';
import PendingStudentConnectionRequestModal from '../../components/PendingStudentConnectionRequestModal';
import SinglePendingStudentConnectionRequestModal from './SinglePendingStudentConnectionRequestModal';

function TutorMyCoursesPage({ setSelectedContent, setSelectedCourse, inProgressCourses, pendingCourses, declinedCourses }) {
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [isSingleStudentPendingCourseRequestDecisionModalOpen, setIsSingleStudentPendingCourseRequestDecisionModalOpen] = useState(false);
    const [isTutorCourseSessionsCourseModalOpen, setIsTutorCourseSessionsCourseModalOpen] = useState(false);
    const [modalCategory, setModalCategory] = useState(null);
    const [selectedPendingCourseForTutorDecision, setSelectedPendingCourseForTutorDecision] = useState(null);

    const [currentPage, setCurrentPage] = useState(1);
    const itemsPerPage = 5;

    const handleCourseClick = (course) => {
        setSelectedCourse(course);
        setSelectedContent('course');
    };

    const handlePendingCourseClick = (course) => {
        setIsSingleStudentPendingCourseRequestDecisionModalOpen(true);
        setSelectedPendingCourseForTutorDecision(course);
        // console.log("Pending course clicked:", course);
    };

    const displayedInProgressCourses = inProgressCourses.slice(0, 3);
    const displayedPendingCourses = pendingCourses.slice(0, 3);
    const displayedDeclinedCourses = declinedCourses.slice(0, 3);

    const openModal = (category) => {
        setModalCategory(category);
        setCurrentPage(1); // Reset to the first page
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
        setModalCategory(null);
    };

    const handleMoreCoursesRequest = (category) => {
        setModalCategory(category);
        setCurrentPage(1);
        setIsTutorCourseSessionsCourseModalOpen(true);
    };

    const paginatedCourses = (courses) => {
        const startIndex = (currentPage - 1) * itemsPerPage;
        return courses.slice(startIndex, startIndex + itemsPerPage);
    };

    const totalPages = (courses) => Math.ceil(courses.length / itemsPerPage);
    return (
        <div className="coursesContainer">
            {/* In Progress Section */}
            <div className="coursesSection largeSection">
                <h2>In Progress</h2>
                <div className="courseCards">
                    {displayedInProgressCourses.map(course => (
                        <div
                            className="courseCard fullLengthCard"
                            key={course.id}
                            onClick={() => handleCourseClick(course)}
                        >
                            <div className="profileSection">
                                <img src={course.profilePicture} alt="Student" className="profileImage" />
                            </div>
                            <div className="courseInfo">
                                <p className="studentName"><b>Student:</b> {course.name}</p>
                                <p className="focus"><b>Focus:</b> {course.focus}</p>
                            </div>
                        </div>
                    ))}
                </div>
                <button className="seeMoreButton" onClick={() => handleMoreCoursesRequest('inProgress')}>See More</button>
            </div>

            {/* Pending Section */}
            <div className="coursesSection largeSection">
                <h2>Pending</h2>
                <div className="courseCards">
                    {displayedPendingCourses.map(course => (
                        <div key={course.id} className="courseCard fullLengthCard" onClick={() => handlePendingCourseClick(course)}>
                            <div className="profileSection">
                                <img src={course.profilePicture} alt="Student" className="profileImage" />
                            </div>
                            <div className="courseInfo">
                                <p className="studentName"><b>Student:</b> {course.studentName}</p>
                                <p className="focus"><b>Focus:</b> {course.focus}</p>
                                <p className="message"><b>Your Message:</b> {course.message}</p>
                            </div>
                        </div>
                    ))}
                </div>
                <button className="seeMoreButton" onClick={() => handleMoreCoursesRequest('pending')}>See More</button>
            </div>

            {/* Declined Section */}
            <div className="coursesSection fullSection">
                <h2>Declined</h2>
                <div className="courseCardsDeclined">
                    {displayedDeclinedCourses.map(course => (
                        <div key={course.id} className="courseCard declinedCards">
                            <div className="profileSection">
                                <img src={course.profilePicture} alt="Tutor" className="profileImage" />
                            </div>
                            <div className="courseInfo">
                                <p className="studentName"><b>Tutor:</b> {course.studentName}</p>
                                <p className="focus"><b>Focus:</b> {course.focus}</p>
                                <p className="reason"><b>Reason for Rejection:</b> {course.reason}</p>
                            </div>
                        </div>
                    ))}
                </div>
                <button className="seeMoreButton" onClick={() => handleMoreCoursesRequest('declined')}>See More</button>
            </div>

            {isTutorCourseSessionsCourseModalOpen && (
                <PendingStudentConnectionRequestModal
                    courses={modalCategory === 'inProgress' ? inProgressCourses
                        : modalCategory === 'pending' ? pendingCourses
                            : declinedCourses}
                    isOpen={isTutorCourseSessionsCourseModalOpen}
                    onClose={() => setIsTutorCourseSessionsCourseModalOpen(false)}
                    itemsPerPage={modalCategory === 'inProgress' ? 5 : 3}
                    currentPage={currentPage}
                    setCurrentPage={setCurrentPage}
                />
            )}
{isSingleStudentPendingCourseRequestDecisionModalOpen && (
<SinglePendingStudentConnectionRequestModal session={
    selectedPendingCourseForTutorDecision
} isOpen={setIsSingleStudentPendingCourseRequestDecisionModalOpen}/>
    )}
