                        <div className="modalCourseCards">
                            {paginatedCourses(
                                modalCategory === 'inProgress' ? inProgressCourses
                                    : modalCategory === 'pending' ? pendingCourses
                                        : declinedCourses
                            ).map(course => (
                                <div key={course.id} className="courseCard fullLengthCard">
