            <style jsx>{`
                .coursesContainer {
                    padding: 20px;
                    background-color: #f8f8f8;
                    display: grid;
                    grid-template-columns: repeat(2, 1fr);
                    grid-template-rows: auto auto;
                    gap: 20px;
                }

                .fullLengthCard {
                    display: flex;
                    flex-grow: 1;
                    width: 100%;
                    height: 100px; /* Set a fixed height for all cards */
                }

                .coursesSection {
                    background-color: #fff;
                    border-radius: 10px;
                    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
                    padding: 20px;
                }

                .largeSection {
                    grid-column: span 1;
                }

                .fullSection {
                    grid-column: span 2;
                }

                h2 {
                    font-size: 24px;
                    color: #333;
                    margin-bottom: 20px;
                }

                .courseCards {
                    display: flex;
                    flex-direction: column;
                    gap: 20px;
                }
                .courseCardsDeclined {
                    display: flex;
                    flex-direction: row;
                    gap: 20px;
                }

                .courseCard {
                    display: flex;
                    background-color: lightgrey;
                    padding: 10px;
                    border-radius: 10px;
                    flex:1;
                }

                .profileSection {
                    margin-right: 15px;
                }

                .profileImage {
                    width: 60px;
                    height: 60px;
                    border-radius: 50%;
                    object-fit: cover;
                }

                .courseInfo {
                    flex: 1;
                }

                .studentName,
                .focus,
                .message,
                .reason {
                    margin: 5px 0;
                    font-size: 14px;
                    color: #555;
                }

                .seeMoreButton {
                    background-color: #007bff;
                    color: white;
                    padding: 10px 15px;
                    border: none;
                    border-radius: 5px;
                    cursor: pointer;
                    font-size: 14px;
                    margin-top: 20px;
                }

                .seeMoreButton:hover {
                    background-color: #0056b3;
                }

                .declinedCards  {
                    width: calc(33.33% - 20px); /* Each card takes up 1/3 of the horizontal space */
                    margin-right: 20px; /* Add some space between cards */
                }

                .declinedCards {
                    display: flex;
                    flex-wrap: wrap;
                }
                .modalOverlay {
                    position: fixed;
                    top: 0;
                    left: 0;
                    width: 100%;
                    height: 100%;
                    background: rgba(0, 0, 0, 0.5);
                    display: flex;
                    justify-content: center;
                    align-items: center;
                }

                .modalContent {
                    background: white;
                    padding: 20px;
                    border-radius: 8px;
                    width: 50%;
                    max-height: 80%;
                    overflow-y: auto;
                }

                .modalCourseCards {
                    display: flex;
                    flex-wrap: wrap;
                    gap: 10px;
                }

                .pagination {
                    display: flex;
                    justify-content: space-between;
                    align-items: center;
                    margin-top: 20px;
                }
                .closeButton {
                    margin-top: 20px;
                    padding: 10px 15px;
                    background: red;
                    color: white;
                    border: none;
                    border-radius: 5px;
                    cursor: pointer;
                }
            `}</style>
        </div>
    );
}

export default TutorMyCoursesPage;
